import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { Card, Avatar, Row, Col, Tooltip, Tag } from 'antd';
import { EyeOutlined, StarTwoTone, ShoppingTwoTone } from '@ant-design/icons';
import axios from '../../../axios';

const Cart = ({ currentUser, setCurrentUser, setCartValue }) => {
	const [cartList, setCartList] = useState([]);
	const { Meta } = Card;
	const { userId } = useParams();
	async function getCartProducts() {
		console.log(`cart product`);
		console.log(`userId`, userId);
		const response = await axios.get(`/user/${userId}/cart`);
		if (response) {
			setCartList(response?.data);
		}
	}
	useEffect(() => {
		if (userId) getCartProducts();
		// eslint-disable-next-line react-hooks/exhaustive-deps
	}, [userId]);

	return (
		<div className="bg-white shadow rounded-lg p-6">
			<div className="flex justify-center">PRODUCTS</div>
			{cartList?.length > 0 && (
				<Row gutter={[24, 24]} className="border">
					<Col xl={4} lg={4} md={4} sm={4} xs={4}>
						{cartList?.map((item) => (
							<Card
								style={{ width: 300 }}
								cover={
									<img
										alt="example"
										src="https://gw.alipayobjects.com/zos/rmsportal/JiqGstEfoWAOHiTxclqi.png"
									/>
								}
								actions={[
									<Tooltip title={item?.product?.product_rating}>
										<StarTwoTone key="rating" twoToneColor="yellow" />
									</Tooltip>,
									<ShoppingTwoTone key="add" />,
									<EyeOutlined key="ellipsis" />,
								]}
							>
								<Meta
									avatar={
										<Avatar
											style={{
												color: 'black',
												backgroundColor: '#00ff66',
											}}
										>
											<span className="capitalize">$ {item?.product?.product_price}</span>
										</Avatar>
									}
									title={
										<div className="w-full flex justify-between">
											<div>{item?.product?.product_name}</div>{' '}
											<div>
												{item?.product?.product_category?.map((category) => {
													let color = category.length === 5 ? 'geekblue' : 'green';
													if (category === 'Children') {
														color = 'volcano';
													}
													if (category === 'Home Accessories') {
														color = 'Yellow';
													}
													return (
														<Tag color={color} key={category}>
															{category.toUpperCase()}
														</Tag>
													);
												})}
											</div>
										</div>
									}
									description={<div className="truncate">{item?.product?.product_description}</div>}
								/>
							</Card>
						))}
					</Col>
					<Col xl={14} lg={14} md={14} sm={24} xs={24}>
						Payment
					</Col>
				</Row>
			)}
		</div>
	);
};
export default Cart;
