import React, { useEffect, useState } from 'react';
import { Card, Avatar, Row, Col } from 'antd';
import { CommentOutlined, PlusCircleOutlined, EyeOutlined, StarTwoTone } from '@ant-design/icons';
import axios from '../../axios';
import '../../App.css';

const Products = ({ currentUser, setCurrentUser }) => {
	const [productList, setProductList] = useState([]);
	const { Meta } = Card;
	useEffect(() => {
		async function checkExistingUser() {
			const res = await axios.get('/user/current');
			if (res?.statusText === 'OK') {
				setCurrentUser(res?.data);
			}
		}
		checkExistingUser();
		// eslint-disable-next-line react-hooks/exhaustive-deps
	}, []);

	async function getProducts() {
		console.log(`here`);
		const req = await axios.get('/product/all');
		if (req) {
			setProductList(req?.data);
		}
	}
	useEffect(() => {
		if (currentUser) getProducts();
	}, [currentUser]);
	console.log(`currentUser`, currentUser);
	return (
		<div className="bg-white shadow rounded-lg p-6">
			<div className="flex justify-center">PRODUCTS</div>
			{productList?.length > 0 && (
				<Row gutter={[24, 24]}>
					{productList?.map((item) => (
						<Col xl={6} lg={6} md={8} sm={24} xs={24}>
							<Card
								style={{ width: 300 }}
								cover={
									<img
										alt="example"
										src="https://gw.alipayobjects.com/zos/rmsportal/JiqGstEfoWAOHiTxclqi.png"
									/>
								}
								actions={[
									<StarTwoTone key="rating" twoToneColor="yellow" />,
									<PlusCircleOutlined key="add" />,
									<EyeOutlined key="ellipsis" />,
								]}
							>
								<Meta
									avatar={
										<Avatar
											style={{
												color: 'black',
												backgroundColor: '#00ff66',
											}}
										>
											<span className="capitalize">$ {item?.product_price}</span>
										</Avatar>
									}
									title={item?.product_name}
									description={<div className="truncate">{item?.product_description}</div>}
								/>
							</Card>
						</Col>
					))}
				</Row>
			)}
		</div>
	);
};
export default Products;
