import React, { useState, useEffect } from 'react';
import { Button } from './Button';
import { Avatar, Menu, Dropdown, Drawer, notification, Badge } from 'antd';
import { ShoppingTwoTone } from '@ant-design/icons';
import { Link, useHistory } from 'react-router-dom';
import axios from '../axios';
import './Navbar.css';

function Navbar({ currentUser, setCurrentUser, cartValue, setCartValue }) {
	const [click, setClick] = useState(false);
	const [button, setButton] = useState(true);
	const [visible, setVisible] = useState(false);
	let history = useHistory();
	async function logoutUser() {
		console.log(`currentUser[0]`, currentUser);
		const req = await axios.post('/user/logout', currentUser[0]);
		if (req) {
			setCurrentUser('');
			notification.open({
				message: 'Come back soon!',
				description: `You have been logged out successfuly`,
			});
			history.push('/');
		}
	}
	async function getCartProducts(id) {
		console.log(`cart product`);
		console.log(`currentUser[0]?._id`, id);
		const response = await axios.get(`/user/${id}/cart`);
		if (response) {
			setCartValue(response?.data?.length);
		}
	}
	useEffect(() => {
		if (currentUser[0]?._id) getCartProducts(currentUser[0]?._id);
	}, [currentUser]);
	const menu = (
		<Menu>
			<Menu.Item
				onClick={() => {
					setVisible(true);
				}}
			>
				User Profile
			</Menu.Item>
			<Menu.Item danger onClick={logoutUser}>
				Log out
			</Menu.Item>
		</Menu>
	);
	const handleClick = () => setClick(!click);
	const closeMobileMenu = () => setClick(false);
	const onClose = () => {
		setVisible(false);
	};
	const showButton = () => {
		if (window.innerWidth <= 960) {
			setButton(false);
		} else {
			setButton(true);
		}
	};

	useEffect(() => {
		showButton();
	}, []);

	window.addEventListener('resize', showButton);

	return (
		<>
			<nav className="navbar">
				<div className="navbar-container">
					<Link to="/" className="navbar-logo" onClick={closeMobileMenu}>
						FlyBuy
					</Link>
					<div className="menu-icon" onClick={handleClick}>
						<i className={click ? 'fas fa-times' : 'fas fa-bars'} />
					</div>
					<ul className={click ? 'nav-menu active' : 'nav-menu'}>
						<li className="nav-item">
							<Link to="/" className="nav-links" onClick={closeMobileMenu}>
								Home
							</Link>
						</li>
						{currentUser && (
							<>
								{/* <li className="nav-item">
									<Link to="/services" className="nav-links" onClick={closeMobileMenu}>
										Services
									</Link>
								</li> */}
								<li className="nav-item">
									<Link to="/products" className="nav-links" onClick={closeMobileMenu}>
										Products
									</Link>
								</li>
								<li className="nav-item">
									<Link to="/cart" className="nav-links" onClick={closeMobileMenu}>
										<Badge count={cartValue}>
											<ShoppingTwoTone className="text-4xl" twoToneColor="#fff" />
										</Badge>
									</Link>
								</li>
							</>
						)}

						<li>
							<Link to="/log-in" className="nav-links-mobile" onClick={closeMobileMenu}>
								Log In/Sign Up
							</Link>
						</li>
					</ul>
					{button && (
						<>
							{currentUser ? (
								<Dropdown overlay={menu}>
									<Avatar
										style={{
											color: 'black',
											backgroundColor: '#00ff66',
										}}
									>
										<span className="capitalize">
											{currentUser[0]?.first_name?.charAt(0)}{' '}
											{currentUser[0]?.last_name?.charAt(0)}
										</span>
									</Avatar>
								</Dropdown>
							) : (
								<Link to="/log-in">
									<Button buttonStyle="btn--outline">LOG IN/SIGN UP</Button>
								</Link>
							)}
						</>
					)}
				</div>
			</nav>

			<Drawer title="Basic Drawer" placement="right" closable={false} onClose={onClose} visible={visible}>
				<p>Some contents...</p>
				<p>Some contents...</p>
				<p>Some contents...</p>
			</Drawer>
		</>
	);
}

export default Navbar;
