import React, { useEffect, useState } from 'react';
import { Card, Avatar, Row, Col, Tooltip, Tag, message } from 'antd';
import { EyeOutlined, StarTwoTone, ShoppingTwoTone } from '@ant-design/icons';
import axios from '../../axios';

const Cart = ({ currentUser, setCurrentUser, setCartValue }) => {
	const [cartList, setCartList] = useState([]);
	const { Meta } = Card;
	async function getCartProducts() {
		console.log(`cart product`);
		console.log(`currentUser[0]?._id`, currentUser[0]?._id);
		const response = await axios.get(`/user/${currentUser[0]?._id}/cart`);
		if (response) {
			setCartList(response?.data);
		}
	}
	useEffect(() => {
		if (currentUser && currentUser[0]?._id) getCartProducts();
		// eslint-disable-next-line react-hooks/exhaustive-deps
	}, [currentUser]);

	return (
		<div className="bg-white shadow rounded-lg p-6">
			<div className="flex justify-center">PRODUCTS</div>
			{cartList?.length > 0 && (
				<Row gutter={[24, 24]}>
					{cartList?.map((item) => (
						<Col xl={6} lg={6} md={8} sm={24} xs={24}>
							<Card
								style={{ width: 300 }}
								cover={
									<img
										alt="example"
										src="https://gw.alipayobjects.com/zos/rmsportal/JiqGstEfoWAOHiTxclqi.png"
									/>
								}
								actions={[
									<Tooltip title={item?.product_rating}>
										<StarTwoTone key="rating" twoToneColor="yellow" />
									</Tooltip>,
									<ShoppingTwoTone key="add" />,
									<EyeOutlined key="ellipsis" />,
								]}
							>
								<Meta
									avatar={
										<Avatar
											style={{
												color: 'black',
												backgroundColor: '#00ff66',
											}}
										>
											<span className="capitalize">$ {item?.product_price}</span>
										</Avatar>
									}
									title={
										<div className="w-full flex justify-between">
											<div>{item?.product_name}</div>{' '}
											<div>
												{item?.product_category?.map((category) => {
													let color = category.length === 5 ? 'geekblue' : 'green';
													if (category === 'Children') {
														color = 'volcano';
													}
													if (category === 'Home Accessories') {
														color = 'Yellow';
													}
													return (
														<Tag color={color} key={category}>
															{category.toUpperCase()}
														</Tag>
													);
												})}
											</div>
										</div>
									}
									description={<div className="truncate">{item?.product_description}</div>}
								/>
							</Card>
						</Col>
					))}
				</Row>
			)}
		</div>
	);
};
export default Cart;
