/* eslint-disable no-unused-expressions */
import React from 'react';
import { Row, Col, Form, Input, Button, notification } from 'antd';
import '../../../App.css';
import axios from '../../../axios';
import Logo from '../../../images/circle-cropped.png';

const Login = () => {
	const [form] = Form.useForm();

	console.log(window.location);
	return (
		<>
			<div className="log-in" style={{ paddingTop: '10rem' }}>
				<Row>
					<Col xl={12} lg={12} md={12} sm={24} xs={24}>
						<img src={Logo} alt="FlyBuy" style={{ width: '25rem', marginLeft: '5rem' }} />
					</Col>
					<Col xl={12} lg={12} md={12} sm={24} xs={24}>
						<div className="mt-8 mx-12">
							<Form
								hideRequiredMark
								name="basic"
								//keep track of what user write in input field
								form={form}
								onFinish={(values) => {
									console.log('values', values);
									// async function checkExistingUser() {
									//   const req = await axios.post("/user/login", values);
									//   if (req) {
									//     console.log("req from deployed database", req);
									//   }
									// }
									// checkExistingUser();
									async function checkExistingUser() {
										await axios.post('/user/find', values).then((res) => {
											console.log('res', res);
											if (res?.data?.length === 0) {
												console.log(`no data`);
												notification.open({
													message: 'User does not exist!',
													description:
														'User with this username does not exist , please sign up.',
												});
											}
										});
									}
									checkExistingUser();
									form?.resetFields();
								}}
							>
								<Form.Item
									label="Username"
									name="username"
									rules={[
										{
											required: true,
											message: 'Please input your username!',
										},
									]}
								>
									<Input />
								</Form.Item>

								<Form.Item
									label="Password"
									name="password"
									rules={[
										{
											required: true,
											message: 'Please input your password!',
										},
									]}
								>
									<Input.Password />
								</Form.Item>

								<Form.Item>
									<Button type="primary" onClick={() => form?.submit()}>
										Submit
									</Button>
								</Form.Item>
							</Form>
						</div>
					</Col>
				</Row>
			</div>
		</>
	);
};

export default Login;
