/* eslint-disable no-unused-expressions */
import React, { useState, useEffect } from 'react';
import { Row, Col, Button, Divider, Card, Tabs, Tag, message } from 'antd';
import { useParams, useHistory } from 'react-router-dom';
import '../../../App.css';
import axios from '../../../axios';
import { CheckCircleFilled } from '@ant-design/icons';

const ViewProducts = ({ setCartValue, currentUser }) => {
	const { Meta } = Card;
	let history = useHistory();
	const [productData, setProductData] = useState();
	const [previewImage, setPreviewImage] = useState('');

	const { TabPane } = Tabs;
	const { productId } = useParams();
	console.log(`productData`, productData);
	async function getProductData() {
		const req = await axios.get(`/product/${productId}`);
		if (req) {
			console.log(`data of particular product`, req?.data);
			setPreviewImage(req?.data[0]?.ImageURLOne?.url);
			setProductData(req?.data[0]);
		}
	}

	useEffect(() => {
		getProductData();
	}, [productId]);

	async function getCartProducts() {
		console.log(`cart product`);
		console.log(`currentUser[0]?._id`, currentUser[0]?._id);
		const response = await axios.get(`/user/${currentUser[0]?._id}/cart`);
		if (response) {
			setCartValue(response?.data?.length);
		}
	}
	async function addToCart(id) {
		console.log(`get cart`);
		const res = await axios.post(`/product/${id}/add/cart`, currentUser[0]);
		if (res) {
			if (res?.data?._id) {
				message.success('Product added to cart successfully');
				getCartProducts();
			}
			console.log(`res`, res);
		}
	}
	return (
		<>
			<div>
				vvbnvh
				<Row gutter={[24, 12]} className="px-12">
					<Col xl={24} lg={24} md={24} sm={24} xs={24}>
						{productData && (
							<>
								<div className=" mt-12 shadow rounded-lg p-4 flex justify-between">
									<div className="font-bold text-lg">{productData?.product_name}</div>
									<div>
										<Button
											disabled={productData?.status === 'Approved'}
											type="primary"
											onClick={() => {
												console.log(`productData`, productData);
												addToCart(productData?._id);
											}}
										>
											<CheckCircleFilled /> Add To Cart
										</Button>
									</div>
								</div>

								<div className="my-6 shadow rounded-lg">
									{productData && (
										<>
											<Row gutter={[24, 12]}>
												<Col xl={8} lg={8} md={8} sm={24} xs={24}>
													<div
														style={{ maxHeight: '470px', overflow: 'auto' }}
														className="cardStyling"
													>
														<Card
															className="m-3 "
															onClick={() => {
																setPreviewImage(productData?.ImageURLOne?.secure_url);
															}}
															hoverable
															style={{ width: 240 }}
															cover={
																<img
																	alt="example"
																	src={productData?.ImageURLOne?.secure_url}
																/>
															}
														>
															<Meta title="View" />
														</Card>
														<Card
															className="m-3 "
															onClick={() => {
																setPreviewImage(productData?.ImageURLTwo?.url);
															}}
															hoverable
															style={{ width: 240 }}
															cover={
																<img
																	alt="example"
																	src={productData?.ImageURLTwo?.url}
																/>
															}
														>
															<Meta title="View" />
														</Card>
														<Card
															className="m-3 "
															onClick={() => {
																setPreviewImage(productData?.ImageURLThree?.url);
															}}
															hoverable
															style={{ width: 240 }}
															cover={
																<img
																	alt="example"
																	src={productData?.ImageURLThree?.url}
																/>
															}
														>
															<Meta title="View" />
														</Card>
														<Card
															className="m-3 "
															onClick={() => {
																setPreviewImage(productData?.ImageURLFour?.url);
															}}
															hoverable
															style={{ width: 240 }}
															cover={
																<img
																	alt="example"
																	src={productData?.ImageURLFour?.url}
																/>
															}
														>
															<Meta title="View" />
														</Card>
														<Card
															className="m-3 "
															onClick={() => {
																setPreviewImage(productData?.ImageURLFive?.url);
															}}
															hoverable
															style={{ width: 240 }}
															cover={
																<img
																	alt="example"
																	src={productData?.ImageURLFive?.url}
																/>
															}
														>
															<Meta title="View" />
														</Card>
													</div>
												</Col>
												<Divider type="vertical" />
												<Col xl={12} lg={12} md={12} sm={24} xs={24}></Col>
											</Row>
										</>
									)}
								</div>
								<div className="my-4 shadow rounded-lg">
									<Tabs
										defaultActiveKey="Description"
										onChange={(key) => {
											console.log(key);
										}}
									>
										<TabPane tab="Description" key="Description">
											{productData && productData?.product_description}
										</TabPane>
										<TabPane tab="Price" key="Price">
											{productData && productData?.product_price}
										</TabPane>
										<TabPane tab="Rating" key="Rating">
											{productData && productData?.product_rating}
										</TabPane>
										<TabPane tab="Category" key="Category">
											{productData &&
												productData?.product_category?.map((item) => {
													let color = item.length === 5 ? 'geekblue' : 'green';
													if (item === 'Children') {
														color = 'volcano';
													}
													if (item === 'Home Accessories') {
														color = 'Yellow';
													}
													return (
														<Tag color={color} key={item}>
															{item.toUpperCase()}
														</Tag>
													);
												})}
										</TabPane>
									</Tabs>
								</div>
							</>
						)}
					</Col>
				</Row>
			</div>
		</>
	);
};

export default ViewProducts;
