import React, { useEffect, useState } from 'react';
import {
	Card,
	Avatar,
	Row,
	Col,
	Tooltip,
	Tag,
	message,
	Carousel,
	Button,
	Popover,
	Result,
	Space,
	Table,
	Input,
	Radio,
} from 'antd';
import { Link, useParams, useHistory } from 'react-router-dom';
import {
	EyeOutlined,
	StarTwoTone,
	ShoppingTwoTone,
	PlusCircleOutlined,
	EyeTwoTone,
	UserOutlined,
	FilterTwoTone,
	HeartTwoTone,
} from '@ant-design/icons';
import { debounce } from 'lodash';
import axios from '../../axios';
import '../../App.css';

const Products = ({ currentUser, setCurrentUser, setCartValue, setWishlistValue }) => {
	const { Search } = Input;
	const [productList, setProductList] = useState([]);
	const [textSearch, setTextSearch] = useState('');
	const [productRetailerList, setProductRetailerList] = useState([]);
	const { userId, retailerId } = useParams();
	const { Meta } = Card;
	const history = useHistory();
	console.log(`productRetailerList`, productRetailerList);
	useEffect(() => {
		async function checkExistingUser() {
			const res = await axios.get(`/user/current/${userId}`);
			console.log(`res`, res);
			if (res?.status === 201) {
				localStorage.setItem('user', JSON.stringify(res?.data[0]));
				setCurrentUser(JSON.parse(localStorage.getItem('user')));
			}
		}
		checkExistingUser();
		// eslint-disable-next-line react-hooks/exhaustive-deps
	}, []);

	async function getProducts() {
		console.log(`here`);
		const req = await axios.get(`/product/all/customer?keyword=${textSearch}`);
		if (req) {
			console.log('dsfdhaatujy', req?.data);
			setProductList(req?.data);
		}
	}
	async function getCartProducts() {
		console.log(`called`);
		console.log(`cart product`);
