import React, { useState, useEffect } from 'react';
import { Button } from './Button';
import { Avatar, Menu, Dropdown, Drawer } from 'antd';
import { Link } from 'react-router-dom';
import axios from '../axios';
import './Navbar.css';

function Navbar({ currentUser }) {
	const [click, setClick] = useState(false);
	const [button, setButton] = useState(true);
	const [visible, setVisible] = useState(false);
	async function logoutUser() {
		const req = await axios.post('/user/login', values);
		if (req) {
			// console.log('req from deployed database', req);
			// if (req?.statusText === 'Created') {
			// 	notification.open({
			// 		message: 'Great Job!',
			// 		description: `New user name:- ${req?.data?.first_name} ${req?.data?.last_name} has been created successfuly`,
			// 	});
			// 	history.push('/log-in');
			// }
		}
	}

	const menu = (
		<Menu>
			<Menu.Item
				onClick={() => {
					setVisible(true);
				}}
			>
				User Profile
			</Menu.Item>
			<Menu.Item danger onClick={logoutUser}>
				Log out
			</Menu.Item>
		</Menu>
	);
	const handleClick = () => setClick(!click);
	const closeMobileMenu = () => setClick(false);
	const onClose = () => {
		setVisible(false);
	};
	const showButton = () => {
		if (window.innerWidth <= 960) {
			setButton(false);
		} else {
			setButton(true);
		}
	};

	useEffect(() => {
		showButton();
	}, []);

	window.addEventListener('resize', showButton);

	return (
		<>
			<nav className="navbar">
				<div className="navbar-container">
					<Link to="/" className="navbar-logo" onClick={closeMobileMenu}>
						FlyBuy
					</Link>
					<div className="menu-icon" onClick={handleClick}>
						<i className={click ? 'fas fa-times' : 'fas fa-bars'} />
					</div>
					<ul className={click ? 'nav-menu active' : 'nav-menu'}>
						<li className="nav-item">
							<Link to="/" className="nav-links" onClick={closeMobileMenu}>
								Home
							</Link>
						</li>
						<li className="nav-item">
							<Link to="/services" className="nav-links" onClick={closeMobileMenu}>
								Services
							</Link>
						</li>
						<li className="nav-item">
							<Link to="/products" className="nav-links" onClick={closeMobileMenu}>
								Products
							</Link>
						</li>

						<li>
							<Link to="/log-in" className="nav-links-mobile" onClick={closeMobileMenu}>
								Log In/Sign Up
							</Link>
						</li>
					</ul>
					{button && (
						<>
							{currentUser ? (
								<Dropdown overlay={menu}>
									<Avatar
										style={{
											color: '#f56a00',
											backgroundColor: '#fde3cf',
										}}
									>
										U
									</Avatar>
								</Dropdown>
							) : (
								<Link to="/log-in">
									<Button buttonStyle="btn--outline">LOG IN/SIGN UP</Button>
								</Link>
							)}
						</>
					)}
				</div>
			</nav>

			<Drawer title="Basic Drawer" placement="right" closable={false} onClose={onClose} visible={visible}>
				<p>Some contents...</p>
				<p>Some contents...</p>
				<p>Some contents...</p>
			</Drawer>
		</>
	);
}

export default Navbar;
