/* eslint-disable no-unused-expressions */
import React, { useState, useEffect } from 'react';
import { Row, Col, Table, Tag, Space } from 'antd';
import { useHistory, useParams } from 'react-router-dom';
import '../../../App.css';
import axios from '../../../axios';
import { EyeTwoTone } from '@ant-design/icons';

const AllOrders = () => {
	const [orderList, setOrderList] = useState([]);
	const { userId } = useParams();
	console.log(`orderList`, orderList);
	async function getOrders() {
		const req = await axios.get(`/orders/all?user=${userId}`);
		if (req) {
			setOrderList(req?.data);
		}
	}
	useEffect(() => {
		getOrders();
	}, []);
	let history = useHistory();
	const columns = [
		{
			title: 'Price',
			dataIndex: 'amount',
			render: (text) => <div>INR {text}</div>,
		},
		{
			title: 'Status',
			dataIndex: 'order_status',
			render: (text) => <div> {text || '-'}</div>,
		},
		{
			title: 'Category',
			dataIndex: 'order_category',
			render: (category) => (
				<div className="w-full flex">
					<Tag color="volcano" key={category}>
						{category.toUpperCase() || 'NONE'}
					</Tag>
				</div>
			),
		},
		{
			title: 'Items',
			dataIndex: 'productsList',
			render: (data) => <div className="w-full flex">{data?.length}</div>,
		},
		{
			key: 'action',
			align: 'right',
			render: (text, record) => (
				<Space size="middle">
					<div
						className="text-blue-400 cursor-pointer"
						onClick={() => {
							history?.replace(`/order/user/${record?._id}`);
						}}
					>
						<EyeTwoTone /> View
					</div>
				</Space>
			),
		},
	];

	return (
		<>
			<div>
				<Row gutter={[24, 12]} className="px-12">
					<Col span={4} />
					<Col xl={18} lg={18} md={18} sm={24} xs={24}>
						<div className=" mt-12 shadow rounded-lg p-4">
							<Table
								onRow={(record, rowIndex) => {
									return {
										onClick: () => {
											console.log('record', record);
										}, // click row
									};
								}}
								scroll={{ x: 400, y: 300 }}
								columns={columns}
								dataSource={orderList}
							/>
						</div>
					</Col>
					<Col span={2} />
				</Row>
			</div>
		</>
	);
};

export default AllOrders;
