/* eslint-disable no-unused-expressions */
import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { Card, Avatar, Row, Col, Tooltip, Tag, Carousel } from 'antd';
import { EyeOutlined, StarTwoTone, ShoppingTwoTone } from '@ant-design/icons';
import axios from '../../../axios';

const Cart = ({ currentUser, setCurrentUser, setCartValue }) => {
	const contentStyle = {
		height: '160px',
		color: '#fff',
		lineHeight: '160px',
		textAlign: 'center',
		background: '#364d79',
	};
	const [cartList, setCartList] = useState([]);
	const [totalValue, setTotalValue] = useState([]);
	const { Meta } = Card;
	const { userId } = useParams();
	async function getCartProducts() {
		console.log(`cart product`);
		console.log(`userId`, userId);
		const response = await axios.get(`/user/${userId}/cart`);
		if (response) {
			setCartList(response?.data);
		}
	}
	console.log(`totalValue`, totalValue);
	useEffect(() => {
		if (userId) getCartProducts();
		// eslint-disable-next-line react-hooks/exhaustive-deps
	}, [userId]);

	useEffect(() => {
		const totalPrice = cartList.reduce(function (total, array) {
			return total + parseInt(array?.product?.product_price);
		}, 0);
		setTotalValue(totalPrice);
	}, [cartList]);
	return (
		<div className="bg-white shadow rounded-lg p-6">
			<div className="flex justify-center">PRODUCTS</div>
			{cartList?.length > 0 && (
				<Row gutter={[24, 24]}>
					<Col xl={8} lg={8} md={8} sm={12} xs={12}>
						{cartList?.map((item) => (
							<div className="m-4">
								<Card
									style={{ width: 300 }}
									cover={
										<Carousel autoplay>
											<div>
												<img
													alt="example"
													src="https://gw.alipayobjects.com/zos/rmsportal/JiqGstEfoWAOHiTxclqi.png"
												/>
											</div>
											<div>
												<img
													alt="example"
													src="https://gw.alipayobjects.com/zos/rmsportal/JiqGstEfoWAOHiTxclqi.png"
												/>
											</div>
											<div>
												<img
													alt="example"
													src="https://gw.alipayobjects.com/zos/rmsportal/JiqGstEfoWAOHiTxclqi.png"
												/>
											</div>
											<div>
												<img
													alt="example"
													src="https://gw.alipayobjects.com/zos/rmsportal/JiqGstEfoWAOHiTxclqi.png"
												/>
											</div>
										</Carousel>
									}
									actions={[
										<Tooltip title={item?.product?.product_rating}>
											<StarTwoTone key="rating" twoToneColor="yellow" />
										</Tooltip>,
										<ShoppingTwoTone key="add" />,
										<EyeOutlined key="ellipsis" />,
									]}
								>
									<Meta
										avatar={
											<Avatar
												style={{
													color: 'black',
													backgroundColor: '#00ff66',
												}}
											>
												<span className="capitalize">$ {item?.product?.product_price}</span>
											</Avatar>
										}
										title={
											<div className="w-full flex justify-between">
												<div>{item?.product?.product_name}</div>{' '}
												<div>
													{item?.product?.product_category?.map((category) => {
														let color = category.length === 5 ? 'geekblue' : 'green';
														if (category === 'Children') {
															color = 'volcano';
														}
														if (category === 'Home Accessories') {
															color = 'Yellow';
														}
														return (
															<Tag color={color} key={category}>
																{category.toUpperCase()}
															</Tag>
														);
													})}
												</div>
											</div>
										}
										description={
											<div className="truncate">{item?.product?.product_description}</div>
										}
									/>
								</Card>
							</div>
						))}
					</Col>
					<Col xl={14} lg={14} md={14} sm={12} xs={12}>
						total:- {cartList && cartList?.length > 0 && totalValue}
					</Col>
				</Row>
			)}
		</div>
	);
};
export default Cart;
