			)}

			<div className="mt-8 mx-2">
				<SubmitButton processing={processing} error={error} disabled={!stripe}>
					Pay INR {offersLength === 0 ? total * 0.75 : 0.75}
				</SubmitButton>
			</div>
		</form>
	);
